pragma solidity >=0.4.21 <0.7.0;

import "./DistributeFunding.sol";

contract CrowdFunding
{
    address private owner;

    uint constant private FUNDING_GOAL = 20 ether;

    mapping (address => uint) private funders;
    uint private num_of_funders;
    uint private current_funding;

    event log_contract_made(address indexed _addres);
    event log_deposit_made(address indexed _addres, uint amount);
    event log_withdraw_made(address indexed _addres, uint amount);
    event log_transfer_made(address indexed _addres, uint amount);

    constructor() public
    {
        owner = msg.sender;

        current_funding = 0;
        num_of_funders = 0;

        emit log_contract_made(msg.sender);
    }

    function is_funding_goal_reached() public view returns (bool)
    {
        if (current_funding < FUNDING_GOAL)
        {
            return false;
        }

        return true;
    }

    function deposit() public payable returns (bool)
    {
        if (true == is_funding_goal_reached())
        {
            return false;
        }

        if (msg.value < 1 ether)
        {
            return false;
        }

        current_funding += msg.value;
        funders[msg.sender] = msg.value;
        num_of_funders += 1;

        emit log_deposit_made(msg.sender, msg.value);

        return true;
    }

    function withdraw(uint withdraw_amount) public returns (uint remaining_funding)
    {
        if (withdraw_amount <= funders[msg.sender])
        {
            funders[msg.sender] -= withdraw_amount;
            msg.sender.transfer(withdraw_amount);
            emit log_withdraw_made(msg.sender, withdraw_amount);
        }

        return funders[msg.sender];
    }

    function transfer_funding(DistributeFunding distribute_funding) external payable returns (bool)
    {
        if (true == is_funding_goal_reached())
        {
            address payable addr = address(uint160(address(distribute_funding)));
            addr.transfer(current_funding);
            return true;
        }

        return false;
    }
}
